import pg from "pg";
// import express from "express"

const db = new pg.Client({
  user: "postgres",
  host: "localhost",
  database: "Keeper",
  password: "123456",
  port: 5432,
});
db.connect();

const result = async()=> await db.query("SELECT * FROM notes ORDER  BY id ASC")


export default result;
